import React, { useState } from 'react';

function Ingredient() {
    const [search, setSearch] = useState('');
    const [searchResults, setSearchResults] = useState([]);

    const handleSearchInputChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const handleSearchSubmit = (e) => {
        e.preventDefault();
        // Ici, vous devriez implémenter la logique de recherche en utilisant la valeur de `searchQuery`
        // Pour cet exemple, je vais simplement simuler des résultats aléatoires
        const randomResults = Array.from({ length: 5 }, (_, index) => ({
            id: index + 1,
            name: `Ingredient ${index + 1}`,
            quantity: `${Math.floor(Math.random() * 100)} grams`,
        }));
        setSearchResults(randomResults);
    };

    return (
        <div>
            <span>
                <h1>Ingredients</h1>
                <form action='submit' method="get">
                    <input type="text" value={search} />
                </form>
            </span>
            
        </div>
    );
}

export default Ingredient;
